import React, { useState } from 'react'
import "./Join.css"
import logo from "../../image/logo.png"
import { Link } from 'react-router-dom' ;


let user;

const sendUser=()=>{
  user=document.getElementById('joinInput').value;
  document.getElementById('joinInput').value="";
}

const Join = () => {

const [name, setname] = useState("");

  return (
    <div className='JoinPage'>
      <div className='JoinContainer'>
      <img src={logo} alt='logo'/>
      <h1>ZEE CHAT</h1>
      {/* <input onChange={(e)=> setname(e.target.value)} placeholder='Enter Your Name' type='text' id='joinInput'  onKeyDown={(event) => {
            if (event.key === 'Enter') {
              sendUser(); // Call the same function as the button
              if (!name) event.preventDefault(); // Prevent navigation if name is empty
            }
          }}/>
      <Link onClick={(event)=>!name?event.preventDefault():null} to="/chat">
        <button id='joinbtn' onClick={sendUser}>Login 
          </button>
      </Link> */}
      <input 
        onChange={(e) => setname(e.target.value)} 
        placeholder="Enter Your Name" 
        type="text" 
        id="joinInput" 
        onKeyDown={(event) => {
          if (event.key === 'Enter') {
            if (name) {
              sendUser(); // Trigger the same function as the button
              document.getElementById("joinbtn").click(); // Programmatically click the button
            } else {
              event.preventDefault(); // Prevent navigation if name is empty
            }
          }
        }}
      />
      <Link 
        onClick={(event) => !name ? event.preventDefault() : null} 
        to="/chat"
      >
        <button id="joinbtn" onClick={sendUser}>Login</button>
      </Link>
      </div>
    </div>
  )
}

export default Join;
export {user};
