import "./Message.css"
import React from 'react'

const Message = ({user,message, classs}) => {
  
    if(user)
    {
        return (
            <div className={`messageBox ${classs}`}>
            {`${user}: ${message}`}
            </div>
        )
    }
    else{
        return(
                <div className={`messageBox ${classs}`}>
                {`You:${message}`}
                </div>
        )
    }
    
}

export default Message
